mapper.select:
select
t.id, t.name, cu.name as customerId, u.name as owner, c.fullname as contactsId, t.stage, t.type, t.source, t.money, t.expectedDate, t.stage, t.type, t.source, a.name as activityId, t.createBy, t.createTime, t.editBy, t.editTime, t.description, t.contactSummary, t.nextContactTime
from tbl_tran t join tbl_user u on t.owner=u.id join tbl_customer cu on t.customerId=cu.id join tbl_contacts c on t.contactsId=c.id left join tbl_activity a on t.activityId=a.id where t.id=#{id}

mapper.update:
update tbl_tran set stage=#{stage},
editBy=#{editBy},
editTime=#{editTime}

where id=#{id}
